import React, { Component } from 'react'
import './style.css'



class Jumbotron extends Component {

    state = {
        title: ['The A-List', 'Season Display', 'Image Search'],
        description: ['The A-List is a MERN stack app that helps musicians connect with each and find the right person for the job. The user simply creates a profile and can then search for musicians based on Location, Style, Instrument and Gender', 'Season Display is a React app that takes the users latitude via Geolocation API and determines whether it is summer or winter in the users current hemisphere of the Earth', 'Image Search is a React app that allows users to search for any term while responding with corresponding images through state in React.'],
        appLink: [],
        ghLink: [],
        currentIndex: 0
    }

    goToPrev = () => {

        this.setState({
            currentIndex: this.state.currentIndex - 1
        })
    }

    goToNext = () => {
        console.log('clicked')

        this.setState({
            currentIndex: this.state.currentIndex + 1
        })
        console.log(this.state.currentIndex)
        if (this.state.currentIndex > 3) {
            this.setState({
                currentIndex: [0]
            })
        }
    };



    render() {
        const { title, currentIndex, description, appLink, ghLink } = this.state;
        return (
            <div className='ui container'>
                <div className="jumbotron" >
                <h1>My Apps</h1>
                <div className='row'>
                    <h2 className="title">{title[currentIndex]}</h2>

                    <p className="description">{description[currentIndex]}</p>
                    <hr className="my-4" />

                    <a className="btn btn-primary btn-lg appLink" href={appLink} role="button">Go to App</a>
                    <a className="btn btn-primary btn-lg ghLink" href={ghLink} style={{float: 'right'}}role="button">Github Repo</a>
                    <div className='row'>
                        <div className='col-sm-4'>
                            <button type="button" id='prev'className="btn btn-success" onClick={this.goToPrev}>Previous</button>
                        </div>
                        <div className='col-sm-4'></div>
                        <div className='col-sm-4'>
                            <button type="button" id='next'className="btn btn-success" onClick={this.goToNext}>Next</button>
                        </div>
                    </div>
                </div>
                </div>
            </div>
        )
    }
}

export default Jumbotron;
