import React, {Component} from 'react';
import Img from '../IMG/portrait.jpg'
import Resume from '../IMG/ZacharyRobinResume2019.docx'
import './style.css'


//change state for contact info 
class Card extends Component{

    state={
        count: 3,
        header: ['From Musician to Developer', 'Contact Info'],
        text: ['My name is Zack Robin I am a web developer with a background in music, recording-technology and entertainment business. My skills Include: - React.js - Javascript - ES6 - HTML5/CSS3 - jQuery - Bootstrap(and other CSS libraries) - Relational and Non-Relational Databases - Node.js - Express - API - MVC app architecture - MERN stack - AWS', '- zrobin88@gmail.com - (407)416-9895', 'Music Section Test'],
        btnText: ['Contact','Go Back', 'Go Back'],
        btnText2: 'Music',
        currentIndex: 0,
        translateValue: 0
    }


    contactClick=(event)=>{
        event.preventDefault();
        if(this.state.currentIndex === 0) {
            return this.setState({
              currentIndex: 1,
              translateValue: 1
            })}
        else if(this.state.currentIndex ===1){
            return this.setState({
                currentIndex:0,
                translateValue: 0
            })
        }
    }

    musicClick=(event)=>{
        event.preventDefault();
        if(this.state.currentIndex === 0) {
            return this.setState({
              currentIndex: 2,
              translateValue: 2
            })}
        else if(this.state.currentIndex ===1){
            return this.setState({
                currentIndex:0,
                translateValue: 0
            })
        }
    }


    render(){
        const{header,text,btnText,btnText2,currentIndex}=this.state
    return (
       <div className='container'>
        <div className="card mb-3m poppins text-light exp"style={{'max-width': '93%'}} id='pf'>
            <div className="row ">
                <div className="col-md-4"style={{padding:'20px'}}>
                    <img src={Img} className="card-img img" alt="..."/>
            </div>
                    <div className="col-md-8" style={{padding:'20px'}}>
                        <div className="card-body">
                            <h5 className="card-title">{header[currentIndex]}</h5>
                            <p className="card-text">{text[currentIndex]}</p>

                            <button type="button" id='contact'onClick={this.contactClick}className="btn  btn-outline-light btn-sm">{btnText[currentIndex]}</button>
                            <button type="button" id='contact'onClick={this.musicClick}className="btn  btn-outline-light btn-sm">{btnText2}</button>    

                        </div>
                    </div>
                </div>
            </div>
            </div>
            )
        }
    }

export default Card;
