import React, { Component } from 'react'
import './style.css'



class Jumbotron extends Component {

    state = {
        title: ['The A-List', 'Season Display', 'Image Search'],
        description: [],
        appLink: [],
        ghLink: [],
        currentIndex: 0
    }

    goToPrev = () => {

    }

    goToNext = () => {
        console.log('clicked')

            if(this.state.currentIndex > this.state.title.length){
                this.setState({currentIndex: [0]})
            }   

        this.setState({
           currentIndex: this.state.currentIndex + 1
        })
        console.log(this.state.currentIndex)
        };



    render(){
        const{title,currentIndex,description,appLink,ghLink}=this.state;
        return (
            <div className='ui container'>
                <div className="jumbotron" >

                    <h1 className="title">{title[currentIndex]}</h1>

                    <p className="description">{description[currentIndex]}</p>
                    <hr className="my-4" />
                    <p>Check it out for yourself!</p>
                    <a className="btn btn-primary btn-lg appLink" href="github.com" role="button">Learn more</a>
                    <a className="btn btn-primary btn-lg ghLink" href="github.com" role="button">Learn more</a>
                </div>
                <button type="button" className="btn btn-success" onClick={this.goToPrev}>Previous</button>
                <button type="button" className="btn btn-success" onClick={this.goToNext}>Next</button>
            </div>
        )
    }
}

export default Jumbotron;
