import React, { Component } from 'react';
import Img from '../IMG/portrait.jpg'
import Resume from '../IMG/ZacharyRobinResume2019.docx'
import './style.css'


//change state for contact info 
class Card extends Component {

    state = {
        count: 3,
        header: ['About Me:', 'Contact Info', 'Web Development Skills and Technologies', 'Music/Production Skills and Examples'],
        text: ['My name is Zack Robin and I am a web developer with a background in music, recording-technology and entertainment business. Music has always been my greatest passion in life and technology has been what advances it. Through out the past two decades, technology has drastically reshaped music from the way busisness is done, to how it is produced and to how consumers listen to it. This was my primary motivation to get into software development. Technology is able to do incredible things in our modern era and its incredibly exciting to work in the midst of it. Through technology, I aim to impact the music industry and the musical experience as a whole. ', '- zrobin88@gmail.com - (407)416-9895', '', 'Music Section Content'],
        skills:[[],[],['React.js','Javascript','ES6', 'HTML5/CSS3', 'jQuery', 'Bootstrap(and other CSS libraries)', 'Relational and Non-Relational Databases', 'Node.js', 'Express','API ','MVC','MERN stack', 'AWS'],[]],
        btnText: ['Contact', 'Go Back', 'Go Back', 'Go Back'],
        btnText2: 'Music & Production',
        btnText3: 'Web Development',
        cardText: ['', '', 'This Portfolio Was Made With React.js'],
        currentIndex: 0,
        translateValue: 0
    }


    contactClick = (event) => {
        event.preventDefault();
        if (this.state.currentIndex === 0) {
            return this.setState({
                currentIndex: 1,
                translateValue: 1
            })
        }
        else if (this.state.currentIndex === 1 || 2 || 3) {
            return this.setState({
                currentIndex: 0,
                translateValue: 0
            })
        }
    }

    musicClick = (event) => {
        event.preventDefault();
        if (this.state.currentIndex === 0 || 1 | 2) {
            return this.setState({
                currentIndex: 3,
                translateValue: 3
            })
        }
        else if (this.state.currentIndex === 1) {
            return this.setState({
                currentIndex: 3,
                translateValue: 3
            })
        }
    }
    devClick = (event) => {
        event.preventDefault();
        if (this.state.currentIndex === 0 || 1 || 3) {
            return this.setState({
                currentIndex: 2,
                translateValue: 2
            })
        }
        else if (this.state.currentIndex === 1) {
            return this.setState({
                currentIndex: 2,
                translateValue: 2
            })
        }
    }


    render() {
        const { header, text, btnText, btnText2, btnText3, cardText, currentIndex } = this.state

        return (
            <div className='container'>
                <div className="card mb-3m poppins text-light exp" style={{ 'max-width': '93%' }} id='pf'>
                    <div className="row ">
                        <div className="col-md-4" style={{ padding: '20px' }}>
                            <img src={Img} className="card-img img" alt="..." />
                        </div>
                        <div className="col-md-8" style={{ padding: '20px' }}>
                            <div className="card-body">
                                <h5 className="card-title">{header[currentIndex]}</h5>
                                <p className="card-text">{text[currentIndex]}</p>
                                <ul> 
                                {this.state.skills.map( skill =>
                                    <li>{skill}</li>

                                )}

                                </ul>
                                <br></br>
                                <p className='card-text'>{cardText[currentIndex]}</p>
                                <button type="button" id='contact' onClick={this.contactClick} className="btn  btn-outline-light btn-sm">{btnText[currentIndex]}</button>
                                <button type="button" id='contact' onClick={this.devClick} className="btn  btn-outline-light btn-sm">{btnText3}</button>
                                <button type="button" id='contact' onClick={this.musicClick} className="btn  btn-outline-light btn-sm">{btnText2}</button>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Card;
