import React, { Component } from 'react'
import './style.css'


class Jumbotron extends Component {

    state = {
        title: ['The A-List', 'Season Display', 'Image Search'],
        description: [],
        appLink: [],
        ghLink: [],
        currentIndex: 0
    }

    goToPrev = () => {

    }

    goToNext = () => {
        this.setState(prevState => ({
            currentIndex: prevState.currentIndex + 1
        }));
    }


    render() {
        return (
            <div className='ui container'>
                <div className="jumbotron" >


                    {
                        this.state.title.map((title, i) => (
                            <h1 className="title" key={i} >{this.state.title}</h1>

                        ))
                    }

                    <p className="description">This is a simple hero unit, a simple jumbotron-style component for calling extra attention to featured content or information.</p>
                    <hr className="my-4" />
                    <p>Check it out for yourself!</p>
                    <a className="btn btn-primary btn-lg appLink" href="github.com" role="button">Learn more</a>
                    <a className="btn btn-primary btn-lg ghLink" href="github.com" role="button">Learn more</a>
                </div>
                <button type="button" className="btn btn-success" goToPrev={this.goToPrev}>Previous</button>
                <button type="button" className="btn btn-success" goToNext={this.goToNext}>Next</button>
            </div>
        )
    }
}

export default Jumbotron;
